CREATE TABLE NOURRITURE ( ID NUMBER(8) NOT NULL , NOM VARCHAR2(10) NOT NULL , TYPE VARCHAR2(10) NOT NULL , PRIX VARCHAR2(10) NOT NULL , VALID NUMBER(30) NOT NULL , PRIMARY KEY (ID));
CREATE TABLE MEDICAMENT (ID NUMBER(8) NOT NULL , NOM VARCHAR2(10) NOT NULL , TYPE VARCHAR2(20) NOT NULL , PRIX VARCHAR2(10) NOT NULL , MALADIE VARCHAR2(20) NOT NULL , QUANTITE VARCHAR2(10) , PRIMARY KEY (ID));